//Delete

const swalWithBootstrapButtons = Swal.mixin({});

function handleDeleteConfirmation(formId) {
    swalWithBootstrapButtons
        .fire({
            title: "Kamu yakin ?",
            text: "Anda tidak akan dapat mengembalikan ini !",
            icon: "warning",
            showCancelButton: true,
            confirmButtonColor: "#3085d6",
            cancelButtonColor: "#d33",
            cancelButtonText: "Batal",
            confirmButtonText: "Hapus",
            reverseButtons: true,
        })
        .then((result) => {
            if (result.isConfirmed) {
                swalWithBootstrapButtons
                    .fire({
                        title: "Dihapus!",
                        text: "File Anda telah dihapus.",
                        icon: "success",
                    })
                    .then(() => {
                        document.getElementById(formId).submit();
                    });
            } else if (result.dismiss === Swal.DismissReason.cancel) {
                swalWithBootstrapButtons.fire({
                    title: "Dibatalkan!",
                    confirmButtonColor: "#3085d6",
                    text: "File Anda aman",
                    icon: "error",
                });
            }
        });
}

//Edit
function handleUpdatedConfirmation(event) {
    event.preventDefault(); // Mencegah aksi default dari tombol submit

    Swal.fire({
        title: "Apakah Anda yakin?",
        text: "Anda ingin memperbarui informasi?",
        icon: "question",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        cancelButtonText: "Cancel!",
        confirmButtonText: "Ya, perbarui!",
        reverseButtons: true,
    }).then((result) => {
        if (result.isConfirmed) {
            Swal.fire({
                title: "Diperbarui!",
                confirmButtonColor: "#3085d6",
                text: "Informasi Anda telah diperbarui.",
                icon: "success",
            }).then(() => {
                document.getElementById("submitButton").form.submit();
            });
        }
    });
}
